        return this._setSessionTimeout(this.config.sessionQuitTimeout)
            .then(() => this._wd.quit())
            .then(() => this.log('kill browser %o', this))
            .then(() => this._setHttpTimeout())
            .catch((err) => this.log(err));
    }

    inspect() {
        return `[${this.id} (${this.sessionId})]`;
    }
