module.exports = class SafeRect {
    static create(rect, imageSize) {
        return new SafeRect(rect, imageSize);
    }

    constructor(rect, imageSize) {
        this._rect = rect;
        this._imageSize = imageSize;
    }

    get left() {
        return this._calcCoord('left');
    }

    get top() {
        return this._calcCoord('top');
    }

    _calcCoord(coord) {
        return Math.max(this._rect[coord], 0);
    }

    get width() {
        return this._calcSize('width', 'left');
    }

    get height() {
        return this._calcSize('height', 'top');
    }

    _calcSize(size, coord) {
        const rectCoord = this._calcCoord(coord);

        return Math.min(this._rect[size], this._imageSize[size] - rectCoord);
    }
};

'use strict';

exports.isFullPage = (image, page, screenshotMode) => {
    switch (screenshotMode) {
        case 'fullpage': return true;
        case 'viewport': return false;
        case 'auto': return compareDimensions(image, page);
    }
};

/**
 * @param {Image} image - PngImg wrapper
 * @param {Object} page - capture meta information object
 * @returns {boolean}
 * @private
 */
function compareDimensions(image, page) {
    const pixelRatio = page.pixelRatio;
    const documentWidth = page.documentWidth * pixelRatio;
    const documentHeight = page.documentHeight * pixelRatio;
    const imageSize = image.getSize();

    return imageSize.height >= documentHeight && imageSize.width >= documentWidth;
}

