var util = require('util'),
    Promise = require('bluebird'),

    StateError = require('../errors/state-error'),

    NO_CLIENT_FUNC = 'ERRNOFUNC';

/**
 * @param {Browser} browser
 * @param {String} script
 * @constructor
 */
