const StateProcessor = require('./state-processor');
const Events = require('../constants/events');
const Image = require('../image');

module.exports = class TestStateProcessor extends StateProcessor {
    constructor(config) {
        super({
            module: require.resolve('./capture-processor/tester'),
            constructorArg: config.system.diffColor
        });

        this._diffColor = config.system.diffColor;
    }

    exec(state, browserSession, page, emit) {
        return super.exec(state, browserSession, page)
            .then((result) => {
                if (!result.equal) {
                    result = this._attachDiffBuilder(result);
                }

                emit(Events.TEST_RESULT, result);
            });
    }

    _attachDiffBuilder(result) {
        return _.extend(result, {
            saveDiffTo: (diffPath) => Image.buildDiff({
                reference: result.referencePath,
                current: result.currentPath,
                diff: diffPath,
                diffColor: this._diffColor,
                tolerance: result.tolerance
            })
        });
    }
};

'use strict';

const EventEmitter = require('events').EventEmitter;
const Events = require('./constants/events');
const logger = require('./utils').logger;

const signalHandler = module.exports = new EventEmitter();

process.on('SIGHUP', handleSignal(1));
process.on('SIGINT', handleSignal(2));
process.on('SIGTERM', handleSignal(15));

let callCount = 0;

function handleSignal(signalNo) {
    const exitCode = 128 + signalNo;

    return () => {
        if (callCount++ > 0) {
            logger.warn('Force quit.');
            process.exit(exitCode);
        }

        logger.warn('Cancelling...');
        signalHandler.emit(Events.INTERRUPT, {exitCode});
    };
}

'use strict';
var _ = require('lodash'),
    GeminiError = require('../errors/gemini-error'),
    chalk = require('chalk'),

    util = require('util');

var REMOVED_OPTIONS_REPLACEMENTS = {
    '--source-root': '--system-source-root',
    '--debug': '--system-debug=true',
    '--coverage': '--system-coverage-enabled=true',
    '--coverage-no-html': '--system-coverage-html=false'
};

var REMOVED_ENV_REPLACEMENTS = {
    'GEMINI_SOURCE_ROOT': 'gemini_system_source_root',
    'GEMINI_DEBUG': 'gemini_system_debug',
    'GEMINI_COVERAGE': 'gemini_system_coverage_enabled',
    'GEMINI_COVERAGE_NO_HTML': 'gemini_system_coverage_html',
    'GEMINI_GRID_URL': 'gemini_grid_url',
    'GEMINI_ROOT_URL': 'gemini_root_url',
    'GEMINI_WINDOW_SIZE': 'gemini_window_size'
};

exports.checkForDeprecations = function() {
    checkForRemovedOptions();
    checkForRemovedEnvVars();
};

function checkForRemovedOptions() {
    _.forEach(REMOVED_OPTIONS_REPLACEMENTS, function(replacement, option) {
        if (_.includes(process.argv, option)) {
            throw new GeminiError(
                util.format('Option %s is removed', option),
                'Instead use ' + replacement
            );
        }
    });
}

function checkForRemovedEnvVars() {
    _.forEach(REMOVED_ENV_REPLACEMENTS, function(replacement, option) {
        if (option in process.env) {
            console.warn(
                'WARNING: %s env var is not longer used. Use %s instead',
                chalk.red(option),
                chalk.cyan(replacement)
            );
        }
    });
}

'use strict';

var chalk = require('chalk'),
    util = require('util'),
    GeminiError = require('../errors/gemini-error');

function handleErrors(error) {
    printErrorHeader(error);
    if (error instanceof GeminiError) {
        handleGeminiError(error);
    } else if (error.status === 13) {
        //unknow wd error
        handleUnknownWdError(error);
    } else {
        console.error(error.stack || error.message);
    }
    // exit code
    return 1;
}

function handleUncaughtExceptions() {
    process.on('uncaughtException', function(error) {
        process.exit(handleErrors(error));
    });
}

function printErrorHeader(e) {
    var message = [chalk.red('Critical error')];

    if (e.state) {
        message.push(util.format('while running "%s"', e.state.fullName));
    }

    if (e.browserId) {
        message.push(util.format('in %s', chalk.underline(e.browserId)));
        if (e.sessionId) {
            message.push(util.format('(%s)', chalk.yellow(e.sessionId)));
        }
    }

    console.error(message.join(' ') + ':');
}

function handleGeminiError(error) {
    console.error(error.message);
    if (error.advice) {
        console.error(chalk.green('To fix:'), error.advice);
    }
}

function handleUnknownWdError(error) {
    console.error(error.cause.value.message);
}

exports.handleErrors = handleErrors;
exports.handleUncaughtExceptions = handleUncaughtExceptions;

'use strict';

const Flat = require('./flat');
const FlatVerbose = require('./flat-verbose');

const make = (Ctr) => {
    return (runner) => {
        const reporter = new Ctr();
        reporter.attachRunner(runner);
    };
};

module.exports = {
    mkFlat: make(Flat),
    mkFlatVerbose: make(FlatVerbose)
};

'use strict';

const _ = require('lodash');
const chalk = require('chalk');

const logger = require('../../utils').logger;
const TestCounter = require('../../test-counter');

const Events = require('../../constants/events');

const ICON_SUCCESS = chalk.green('✓');
const ICON_NOT_UPDATED = chalk.gray('✓');
const ICON_FAIL = chalk.red('✘');
const ICON_WARN = chalk.bold.yellow('!');
const ICON_RETRY = chalk.yellow('⟳');

module.exports = class Flat {
    constructor() {
        this._counter = new TestCounter();
    }

    attachRunner(runner) {
        runner.on(Events.TEST_RESULT, this._onTestResult.bind(this));
        runner.on(Events.UPDATE_RESULT, this._onUpdateResult.bind(this));
        runner.on(Events.RETRY, this._onRetry.bind(this));
        runner.on(Events.ERROR, this._onError.bind(this));
        runner.on(Events.WARNING, this._onWarning.bind(this));
        runner.on(Events.END, this._onEnd.bind(this));
        runner.on(Events.INFO, this._onInfo.bind(this));
        runner.on(Events.SKIP_STATE, this._onSkipState.bind(this));
    }

    _onTestResult(result) {
        const handler = result.equal ? this._onPassed : this._onError;
        handler.call(this, result);
    }

    _onUpdateResult(result) {
        const handler = result.updated ? this._onUpdated : this._onNotUpdated;
        handler.call(this, result);
    }

    _onRetry(result) {
        logger.log(`${ICON_RETRY} ${this._formatRetryInfo(result)}`);
        this._logError(result);

        this._counter.onRetry(result);
    }

    _onPassed(result) {
        logger.log(`${ICON_SUCCESS} ${this._formatStateInfo(result)}`);
        this._counter.onPassed(result);
    }

    _onUpdated(result) {
        logger.log(`${ICON_SUCCESS} ${this._formatStateInfo(result)}`);
        this._counter.onUpdated(result);
    }

    _onNotUpdated(result) {
        logger.log(`${ICON_NOT_UPDATED} ${this._formatStateInfo(result)}`);
        this._counter.onPassed(result);
    }

    _onError(result) {
        logger.log(`${ICON_FAIL} ${this._formatStateInfo(result)}`);
        this._logError(result);

        this._counter.onFailed(result);
    }

    _onWarning(result) {
        logger.log(`${ICON_WARN} ${this._formatStateInfo(result)}`);
        logger.warn(result.message);

        this._counter.onSkipped(result);
    }

    _onSkipState(result) {
        const skipReason = result.suite.skipComment
            ? chalk.yellow('reason: ' + result.suite.skipComment)
            : '';

        logger.log(`${ICON_WARN} ${this._formatStateInfo(result)} ${skipReason}`);

        this._counter.onSkipped(result);
    }

    _onInfo(result) {
        logger.warn(result.message);
    }

    _onEnd() {
        const result = this._counter.getResult();

        const message = [
            'Total: ' + chalk.underline(result.total),
            result.updated && 'Updated: ' + chalk.green(result.updated),
            'Passed: ' + (result.updated && chalk.grey(result.passed) || chalk.green(result.passed)),
            'Failed: ' + chalk.red(result.failed),
            'Skipped: ' + chalk.cyan(result.skipped),
            'Retries: ' + chalk.cyan(result.retries)
        ];

        logger.log(_.compact(message).join(' '));
    }

    _logError(result) {
        if (result.message) {
            logger.error(result.message);
        }

        if (result.originalError && result.originalError.stack) {
            logger.error(result.originalError.stack);
        }
    }

    _formatRetryInfo(result) {
        const stateInfo = this._formatStateInfo(result);
        const retriesLeft = result.retriesLeft;

        return `${stateInfo} will be retried. Retries left: ${chalk.yellow(retriesLeft)}`;
    }

    _formatStateInfo(result) {
        const suite = result.suite.path.join(' ');
        const state = result.state && chalk.underline(result.state.name);
        const browserId = `[${chalk.yellow(result.browserId)}]`;

        return _([suite, state, browserId]).compact().join(' ');
    }
};

'use strict';

const _ = require('lodash');
const chalk = require('chalk');
const Flat = require('./flat');

module.exports = class FlatVerbose extends Flat {
    _formatStateInfo(result) {
        const suite = result.suite.path.join(' ');

        const state = result.state
            ? `${chalk.underline(result.state.name)}`
            : '';

        const session = result.suite.skipped
            ? ''
            : `: ${chalk.blue(result.sessionId || 'unknown session id')}`;

        const browser = `[${chalk.yellow(result.browserId)}${session}]`;

        return _([suite, state, browser]).compact().join(' ');
    }
};

'use strict';

const CaptureSession = require('../capture-session');
const temp = require('../temp');
const Promise = require('bluebird');

Promise.promisifyAll(require('fs-extra'));

module.exports = (args, cb) => {
    temp.attach(args.temp);

    const captureProcessor = require(args.captureProcessorInfo.module)
        .create(args.captureProcessorInfo.constructorArg);

    return CaptureSession.fromObject(args.browserSession)
        .then(browserSession => browserSession.capture(args.page))
        .then(capture => captureProcessor.exec(capture, args.execOpts))
        .then(cb.bind(null, null), cb);
};
